import logo from './logo.svg';
import './App.css';
import TaskList from './Components/TaskList/TaskList'
import AddTask from './Components/AddTask/AddTask'
import TaskStatistic from './Components/TaskStatistic/TaskStatistic'
import { useState } from 'react'


const App = () => {


  const createTask = (title, text) => {
    const time = new Date()
    return {
      id: time.getTime(),
      title,
      text,
      status: 0,
      is_selected: false
    }
  }

  const [tasks, setTasks] = useState([])

  const addTask = (title, text) => {
    const newTask = createTask(title, text)
    setTasks([...tasks, newTask])
  }

  const changeStatus = (id, status) => {
    setTasks(tasks.map(task => {
      if(task.id === id) task.status = status
      return task
    }))
  }

  const selectAll = () => {
    const is_find = tasks.find((task) => {
      return !task.is_selected
    }) ? true : false
    setTasks(tasks.map((task) => {
      task.is_selected = is_find
      return task
    }))
  }

  const changeSelected = (id) => {
    setTasks(tasks.map(task => {
      if(task.id === id) task.is_selected = !task.is_selected
      return task
    }))
  }

  return (
    <div>
      <div className='header'>
        <AddTask addTask={addTask}/>
        <TaskStatistic tasksStatistic={tasks}/>
      </div>

      <TaskList tasks={tasks} changeSelected={changeSelected} changeStatus={changeStatus} selectAll={selectAll}/>
    </div>
  )
}

export default App;
