import logo from './logo.svg';
import './App.css';
import TaskList from './Components/TaskList/TaskList'
import AddTask from './Components/AddTask/AddTask'
import { useState } from 'react'


const App = () => {
  const statuses = ["Не выполнено", "В работе", "Выполнено"]

  const createTask = (title, text) => {
    const time = new Date()
    return {
      id: time.getTime(),
      title,
      text,
      status: statuses[0]
    }
  }

  const [tasks, setTasks] = useState([])

  const addTask = (title, text) => {
    const newTask = createTask(title, text)
    setTasks([...tasks, newTask])
  }

  const changeStatuse = (id, status) => {
    setTasks(tasks.map(task => {
      if(task.id === id) task.status = statuses[status]
      return task
    }))
  }

  return (
    <div>
      <AddTask addTask={addTask}/>
      <TaskList tasks={tasks} statuses={statuses}/>
    </div>
  )
}

export default App;
