import logo from './logo.svg';
import './App.css';
import DealList from './Components/DealList/DealList'
import AddDeal from './Components/AddDeal/AddDeal'
import { useState } from 'react'


const App = () => {
  const statuses = ["Не выполнено", "В работе", "Выполнено"]

  const createDeal = (title, text) => {
    const time = new Date()
    return {
      id: time.getTime(),
      title,
      text,
      status: statuses.not_complete
    }
  }

  const [deals, setDeals] = useState([])

  const addDeal = (title, text) => {
    const newDeal = createDeal(title, text)
    setDeals([...deals, newDeal])
  }

  return (
    <div>
      <AddDeal addDeal={addDeal}/>
      <DealList deals={deals} statuses={statuses}/>
    </div>
  )
}

export default App;
